import React, { useState, useEffect } from 'react';
import Axios from 'axios';

const SignUpForm = () => {
  const [users, setUsers] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    username: '',
    password: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSignUpSubmit = async (e) => {
    e.preventDefault();

    const isUsernameTaken = users.some((user) => user.username === formData.username);

    if (isUsernameTaken) {
      // Handle the case where the username is already taken
      alert('Username is already taken');
    } else {
      const newUser = {
        name: formData.name,
        email: formData.email,
        username: formData.username,
        password: formData.password,
      };

      // Simulate a POST request to a JSON database using Axios
      try {
        await Axios.post('your-api-endpoint-for-users', newUser);
        alert('Sign-up successful');
        setFormData({
          name: '',
          email: '',
          username: '',
          password: '',
        });
      } catch (error) {
        console.error('Error during sign-up:', error);
        // Handle the error as needed
      }
    }
  };

  useEffect(() => {
    Axios.get('your-api-endpoint-for-users')
      .then((response) => {
        setUsers(response.data.users);
      })
      .catch((error) => {
        console.error('Error fetching users data:', error);
      });
  }, []);

  return (
    <div>
      <h2>Sign-Up Form</h2>
      <form onSubmit={handleSignUpSubmit}>
        <div>
          <input
            type="text"
            name="name"
            placeholder="Full Name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={formData.username}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <button type="submit">Sign Up</button>
        </div>
      </form>
    </div>
  );
};

export default SignUpForm;
