import React from 'react';
import './LoginFormY.css'
import { Link } from 'react-router-dom/cjs/react-router-dom';
import { useFormik } from 'formik';
import { simpleFormSchema } from '../schemas';
// import Navbar from '../Pages/Navbar';

const initialValues = {
  username: '',
  password: '',
  confirm_password: ''
}

const LoginFormY = () => {
  const [loginError, setLoginError] = useState(null); // State to handle login errors

  const { values, errors, handleBlur, touched, handleChange, handleSubmit } = useFormik({
    initialValues: initialValues,
    validationSchema: simpleFormSchema,
    onSubmit: async (values, actions) => {
      try {
        const response = await axios.post('/api/login', values); // Replace '/api/login' with your server endpoint
        console.log(response.data); // Assuming the server responds with the user data

        // Clear the form
        actions.resetForm();
        setLoginError(null); // Clear any previous login errors
      } catch (error) {
        setLoginError('Login failed. Please check your credentials.'); // Handle login error
      }
    }
  });

  return (
    // ...
    <div className="form-second">
      <h2>Login Form</h2>
      {loginError && <p className="login-error">{loginError}</p>}
      <form onSubmit={handleSubmit}>
        {/* ... Your form input fields */}
        <div className="Form-second-inputBox">
          <input type="submit" value="Login" />
        </div>
      </form>
    </div>
  );
}
