      "price": 22000
    },
    {
      "name": "Car 30",
      "img": "car30.jpg",
      "price": 29000
    }
  ]
const CarSeller = () => {
    const [currentPage, setCurrentPage] = useState(1);
    const [searchQuery, setSearchQuery] = useState('');
    const carsPerPage = 6;
  
    // Filter cars based on the search query
    const filteredCars = dummyCarData.filter((car) =>
      car.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
  
    const totalPages = Math.ceil(filteredCars.length / carsPerPage);
    const startIndex = (currentPage - 1) * carsPerPage;
    const endIndex = startIndex + carsPerPage;
    const displayedCars = filteredCars.slice(startIndex, endIndex);
  
    const handlePageChange = (newPage) => {
      setCurrentPage(newPage);
    };
  
    const handleSearchChange = (e) => {
      setSearchQuery(e.target.value);
      setCurrentPage(1); // Reset to the first page when a new search query is entered
    };
  
    return (
      <div className="app">
        <h1>Seller App</h1>
        <div className="card-capacity">
          {displayedCars.length} from {filteredCars.length}
        </div>
        <input
          type="text"
          placeholder="Search by car name..."
          value={searchQuery}
          onChange={handleSearchChange}
        />
        <CarList cars={displayedCars} />
        <Navigation
          currentPage={currentPage}
          totalPages={totalPages}
          onPageChange={handlePageChange}
        />
      </div>
    );
  }

export default CarSeller;
