import React, { useState } from 'react';
import './TimerLoading.css'
// import ReactDOM from "react-dom";
import { CountdownCircleTimer } from "react-countdown-circle-timer";

const renderTime = ({ remainingTime }) => {
    if (remainingTime === 0) {
      return <div className="timer">Too late...</div>;
    }
  
    return (
      <div className="timer">
        <div className="text">Remaining</div>
        <div className="value">{remainingTime}</div>
        <div className="text">seconds</div>
      </div>
    );
  };
  
  const options = [10, 20, 30, 40, 50];
const TimerLoading = () => {
    const [selectedTime, setSelectedTime] = useState(10);
    const [isTimerActive, setIsTimerActive] = useState(false);
  
    const handleSelectChange = (e) => {
      const newValue = parseInt(e.target.value);
      setSelectedTime(newValue);
      setIsTimerActive(false); // Stop the timer when a new value is selected
    };
  
    const handleResetClick = () => {
      setIsTimerActive(true); // Start the timer when the "Reset" button is clicked
    };
  
    return (
      <div className="App">
        <h1>
          <a
            href="https://www.reactlabs.ai/"
            target="_blank"
            rel="noopener noreferrer"
          >
            React Labs
          </a>
          <br />
          ReactJS Assignment
        </h1>
        <div className="timer-wrapper">
          <CountdownCircleTimer
            key={selectedTime} // Add a key to the timer to force a re-render when selectedTime changes
            isPlaying={isTimerActive}
            duration={selectedTime}
            colors={[["#004777", 0.33], ["#F7B801", 0.33], ["#A30000"]]}
            onComplete={() => setIsTimerActive(false)} // Stop the timer when it completes
          >
            {renderTime}
          </CountdownCircleTimer>
          <div style={{ display: "flex" }}>
            <select value={selectedTime} onChange={handleSelectChange}>
              {options.map((opt) => (
                <option value={opt} key={opt}>
                  {opt + " sec"}
                </option>
              ))}
            </select>
            <button onClick={handleResetClick} className='btn'>Reset</button>
          </div>
          <h3>Can you get this reset button to work?</h3>
          <h4>The reset button should reset as per the time selected in the dropdown</h4>
        </div>
      </div>
    );
  }

export default TimerLoading;
