import React, { useState } from 'react';
import './LoginFormY.css';
import { Link } from 'react-router-dom';
import { useFormik } from 'formik';
import Axios from 'axios';
import { simpleFormSchema } from '../schemas';

const initialValues = {
  username: '',
  password: '',
};

const LoginFormY = () => {
  const [formValues, setFormValues] = useState(initialValues);

  const clearInputFields = () => {
    setFormValues(initialValues);
  };

  const { errors, handleBlur, touched, handleChange, handleSubmit } = useFormik({
    initialValues: formValues, // Use the state values
    validationSchema: simpleFormSchema,
    onSubmit: async (values) => {
      try {
        await Axios.post('https://dashboard-3frk.onrender.com/loginData', values);
        
        alert('Login successful');

        clearInputFields(); // Manually clear input fields by resetting the state

      } catch (error) {
        console.error('Error during login:', error);
      }
    },
  });

  return (
    <>
      <section className='Form-second-main-section'>
        <div className="Form-second-inputBox">
          <input
            type="text"
            autoComplete='off'
            placeholder="Username"
            value={formValues.username}
            onChange={(e) => {
              handleChange(e);
              setFormValues({ ...formValues, username: e.target.value });
            }}
            onBlur={handleBlur}
            minLength="4"
            maxLength="20"
            id="username"
            name="username"
          />
          {errors.username && touched.username ? (<p>{errors.username}</p>) : null}
        </div>
        <div className="Form-second-inputBox">
          <input
            type="password"
            autoComplete='off'
            placeholder="Password"
            value={formValues.password}
            onChange={(e) => {
              handleChange(e);
              setFormValues({ ...formValues, password: e.target.value });
            }}
            onBlur={handleBlur}
            minLength="6"
            maxLength="50"
            id="password"
            name="password"
          />
          {errors.password && touched.password ? (<p>{errors.password}</p>) : null}
        </div>
        <div className="Form-second-inputBox">
          <input type="submit" value="Login" />
        </div>
        <p className="Form-second-forget">
          Forgot password ? <Link to="#">Click Here</Link>
        </p>
        <p className="Form-second-forget">
          Don't have an account ? <Link to="#">Sign up</Link>
        </p>
      </section>
    </>
  );
};

export default LoginFormY;
