import React, { useState } from 'react';
import styles from './TimerLoading.module.css';
import { CountdownCircleTimer } from 'react-countdown-circle-timer';
import Navbar from '../Pages/Navbar';

const options = [10, 20, 30, 40, 50];
const TimerLoading = () => {
  const [selectedTime, setSelectedTime] = useState(10);
  const [isTimerActive, setIsTimerActive] = useState(false);

  const handleSelectChange = (e) => {
    const newValue = parseInt(e.target.value);
    setSelectedTime(newValue);
    setIsTimerActive(false); // Stop the timer when a new value is selected
  };

  const handleResetClick = () => {
    setIsTimerActive(true); // Start the timer when the "Reset" button is clicked
  };

  const getColorBasedOnTime = (remainingTime) => {
    // Define color stops and their respective time intervals
    const colorStops = [
      { color: '#004777', threshold: 0.5 }, // Change to this color at 50% of the duration
      { color: '#F7B801', threshold: 0.2 }, // Change to this color at 20% of the duration
      { color: '#A30000', threshold: 0 },   // Change to this color at 0% of the duration
    ];

    // Find the color stop whose threshold matches the current remaining time
    const matchingStop = colorStops.find((stop) => remainingTime <= selectedTime * stop.threshold);

    // Return the color of the matching stop or the last color if none matches
    return matchingStop ? matchingStop.color : colorStops[colorStops.length - 1].color;
  };

  return (
    <>
      <Navbar />
      <div className={styles.App}>
        <div className={styles.timer_wrapper}>
          <CountdownCircleTimer
            key={selectedTime}
            isPlaying={isTimerActive}
            duration={selectedTime}
            colors={[[getColorBasedOnTime, 0.33]]} // Use getColorBasedOnTime function to set color
            onComplete={() => setIsTimerActive(false)}
          >
            {({ remainingTime }) => (
              <div className={styles.timer} style={{ color: getColorBasedOnTime(remainingTime) }}>
                <div className={styles.text}>Remaining</div>
                <div className={styles.value}>{remainingTime}</div>
                <div className={styles.text}>seconds</div>
              </div>
            )}
          </CountdownCircleTimer>
          <div style={{ display: 'flex' }}>
            <select value={selectedTime} onChange={handleSelectChange}>
              {options.map((opt) => (
                <option value={opt} key={opt}>
                  {opt + ' sec'}
                </option>
              ))}
            </select>
            <button onClick={handleResetClick} className='btn btn-danger mx-2'>
              Reset
            </button>
          </div>
          <h3>Can you get this reset button to work?</h3>
          <h4>The reset button should reset as per the time selected in the dropdown</h4>
        </div>
      </div>
    </>
  );
};

export default TimerLoading;
